	private static BlockFamily.Builder familyBuilder(Block baseBlock) {
		BlockFamily.Builder builder = new BlockFamily.Builder(baseBlock);
		BlockFamily blockFamily = MAP.put(baseBlock, builder.getFamily());
		if (blockFamily != null)
			throw new IllegalStateException("Duplicate family definition for " + ForgeRegistries.BLOCKS.getKey(baseBlock));
		return builder;
	}

	private static BlockFamily.Builder ultraFamilyBuilder(Block baseBlock) {
		BlockFamily.Builder builder = new BlockFamily.Builder(baseBlock);
		BlockFamily blockFamily = ULTRA_MAP.put(baseBlock, builder.getFamily());
		if (blockFamily != null)
			throw new IllegalStateException("Duplicate family definition for " + ForgeRegistries.BLOCKS.getKey(baseBlock));
		return builder;
	}

	public static @NotNull Stream<BlockFamily> getAllFamilies() {
		return MAP.values().stream();
	}
	public static @NotNull Stream<BlockFamily> getAllUltraFamilies() {
		return ULTRA_MAP.values().stream();
	}
